import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import '/Users/kilverthestebanclarosordonez/Downloads/GUARDIAN-UCAT/frontend/src/styles/Login.css';

const Login = ({ onLogin }) => {
    const navigate = useNavigate();
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();
        const userData = { email, password };

        try {
            const response = await fetch('http://localhost:5000/api/login', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(userData),
            });

            if (response.ok) {
                onLogin();
                navigate('/'); // Redirige al menú principal
            } else {
                const errorMessage = await response.text();
                setError(errorMessage);
            }
        } catch (error) {
            console.error('Error en el login:', error);
            setError('Error al iniciar sesión. Por favor intenta nuevamente.');
        }
    };

    return (
        <div className="auth-page">
            <div className="auth-container">
                <h2>Iniciar Sesión</h2>
                {error && <p className="error-message">{error}</p>}
                <form onSubmit={handleLogin}>
                    <div className="input-group">
                        <input
                            type="email"
                            placeholder="Correo electrónico"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                        />
                    </div>
                    <div className="input-group">
                        <input
                            type="password"
                            placeholder="Contraseña"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    </div>
                    <button type="submit" className="auth-button">Ingresar</button>
                </form>
                <div className="auth-links">
                    <Link to="/register" className="auth-link">¿No tienes cuenta? Regístrate aquí</Link>
                    <Link to="/recover-password" className="auth-link">Recuperar contraseña</Link>
                </div>
            </div>
        </div>
    );
};

export default Login;
